// Components/User.js
import React, { useState, useEffect } from 'react';
import './user.css';
import Comments from './comments';

const User = ({ id, email, name, onDelete, onUpdate }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [newName, setNewName] = useState(name);
  const [newEmail, setNewEmail] = useState(email);
  const [showComments, setShowComments] = useState(false);
  const [photoUrl, setPhotoUrl] = useState('');

  useEffect(() => {
    // Kullanıcıya bir fotoğraf atamak için API'den veri çekiyoruz
    fetch(`https://jsonplaceholder.typicode.com/albums/1/photos`)
      .then((response) => response.json())
      .then((photos) => {
        // Kullanıcı ID'sine göre fotoğrafı alıyoruz
        setPhotoUrl(photos[id % photos.length]?.thumbnailUrl);
      })
      .catch((error) => console.log('Error fetching photos:', error));
  }, [id]);

  const handleDelete = () => {
    onDelete(id);
  };

  const handleEdit = () => {
    if (isEditing) {
      onUpdate(id, newName, newEmail);
    }
    setIsEditing(!isEditing);
  };

  return (
    <div className='list'>
      {isEditing ? (
        <>
          <input
            type="text"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
          />
          <input
            type="email"
            value={newEmail}
            onChange={(e) => setNewEmail(e.target.value)}
          />
        </>
      ) : (
        <>
          <img src={photoUrl} alt={`${name}'s avatar`} />
          <span>{name}</span>
          <span>{email}</span>
        </>
      )}
      <span>
        <button onClick={handleEdit}>
          {isEditing ? 'Update' : 'Update'}
        </button>
        <button onClick={handleDelete}>Delete</button>
      </span>
      <button onClick={() => setShowComments(!showComments)}>
        {showComments ? 'Hide Comments' : 'Show Comments'}
      </button>
      {showComments && <Comments postId={id} />}
    </div>
  );
};

export default User;
