import React, { useEffect, useState } from 'react';
import './App.css';
import User from './Components/User';
import AddUser from './Components/AddUser';
import UpdateUser from './Components/UpdateUser';
function App() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const res = await fetch('https://jsonplaceholder.typicode.com/users');
      const data = await res.json();
      setUsers(data);
    } catch (err) {
      console.log(err);
    }
  };

  const onAdd = async (name, email) => {
    try {
      const res = await fetch('https://jsonplaceholder.typicode.com/users', {
        method: 'POST',
        body: JSON.stringify({
          name: name,
          email: email,
        }),
        headers: {
          'Content-type': 'application/json; charset=UTF-8',
        },
      });

      if (res.status === 201) {
        const data = await res.json();
        setUsers((users) => [...users, data]);
      }
    } catch (err) {
      console.log(err);
    }
  };

  const onUpdate = async (userId, name, email) => {
    try {
      const res = await fetch(`https://jsonplaceholder.typicode.com/users/${userId}`, {
        method: 'PUT',
        body: JSON.stringify({
          id: userId,
          name: name,
          email: email,
        }),
        headers: {
          'Content-type': 'application/json; charset=UTF-8',
        },
      });

      if (res.status === 200) {
        const updatedUser = await res.json();
        setUsers((users) =>
          users.map((user) => (user.id === userId ? updatedUser : user))
        );
      }
    } catch (err) {
      console.log(err);
    }
  };

  const onDelete = async (id) => {
    console.log('Deleting user with ID:', id); // Moved the log statement here

    try {
      const res = await fetch(`https://jsonplaceholder.typicode.com/users/${id}`, {
        method: 'DELETE',
      });

      if (res.status === 200) {
        setUsers(users.filter((user) => user.id !== id));
      }
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="App">
      <h3>React CRUD Using JSONPlaceholder</h3>
      <AddUser onAdd={onAdd} />
      <div>
        {users.map((user) => (
          <User
            id={user.id}
            key={user.id}
            name={user.name}
            email={user.email}
            onDelete={onDelete}
            onUpdate={onUpdate}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
