// Components/UserTable.js
import React, { useState } from 'react';
import './userTable.css';

const UserTable = ({ users, onDelete, onUpdate }) => {
  const [editUserId, setEditUserId] = useState(null);
  const [newName, setNewName] = useState('');
  const [newEmail, setNewEmail] = useState('');

  const handleEditClick = (user) => {
    setEditUserId(user.id);
    setNewName(user.name);
    setNewEmail(user.email);
  };

  const handleUpdateClick = () => {
    if (editUserId) {
      onUpdate(editUserId, newName, newEmail);
      setEditUserId(null);
      setNewName('');
      setNewEmail('');
    }
  };

  return (
    <table className="user-table">
      <thead>
        <tr>
          <th>ID</th>
          <th>Name</th>
          <th>Email</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {users.map((user) => (
          <tr key={user.id}>
            <td>{user.id}</td>
            <td>
              {editUserId === user.id ? (
                <input
                  type="text"
                  value={newName}
                  onChange={(e) => setNewName(e.target.value)}
                />
              ) : (
                user.name
              )}
            </td>
            <td>
              {editUserId === user.id ? (
                <input
                  type="email"
                  value={newEmail}
                  onChange={(e) => setNewEmail(e.target.value)}
                />
              ) : (
                user.email
              )}
            </td>
            <td>
              {editUserId === user.id ? (
                <button className="update-btn" onClick={handleUpdateClick}>
                  Update
                </button>
              ) : (
                <button className="edit-btn" onClick={() => handleEditClick(user)}>
                  Edit
                </button>
              )}
              <button className="delete-btn" onClick={() => onDelete(user.id)}>
                Delete
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default UserTable;
