import React from 'react'
import './user.css'
import './AddUser'
import './UpdateUser'
const User = ({id,email,name,onDelete}) => {

const handleDelete = () => {
onDelete(id);
}

const handleUpdate = () => {
  const newName = prompt("Enter new name:", name);
  const newEmail = prompt("Enter new email:", email);
  if (newName && newEmail) {
    onUpdate(id, newName, newEmail);
  }
};


  return (
    <div className='list'>
     <span>{name}</span>
     <span>{email}</span>
     <span>
    
      <button onClick={handleDelete}> delete </button>
        <button onClick={handleUpdate}>Update</button>
     </span>
    </div>
  )
}

export default User
