import React, { useState, useEffect } from 'react';
import './userTable.css';

const UserTable = ({ users, onDelete, onUpdate }) => {
  const [editUserId, setEditUserId] = useState(null);
  const [newName, setNewName] = useState('');
  const [newEmail, setNewEmail] = useState('');
  const [comments, setComments] = useState({});

  useEffect(() => {
    const fetchComments = async () => {
      try {
        for (const user of users) {
          const res = await fetch(`https://jsonplaceholder.typicode.com/posts/${user.id}/comments`);
          const data = await res.json();
          setComments((prevComments) => ({
            ...prevComments,
            [user.id]: data
          }));
        }
      } catch (err) {
        console.log('Error fetching comments:', err);
      }
    };

    fetchComments();
  }, [users]);

  const handleEditClick = (user) => {
    setEditUserId(user.id);
    setNewName(user.name);
    setNewEmail(user.email);
  };

  const handleUpdateClick = () => {
    if (editUserId) {
      onUpdate(editUserId, newName, newEmail);
      setEditUserId(null);
      setNewName('');
      setNewEmail('');
    }
  };

  return (
    <table className="user-table">
      <thead>
        <tr>
          <th>ID</th>
          <th>Name</th>
          <th>Email</th>
          <th>Comments</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {users.map((user) => (
          <tr key={user.id}>
            <td>{user.id}</td>
            <td>
              {editUserId === user.id ? (
                <input
                  type="text"
                  value={newName}
                  onChange={(e) => setNewName(e.target.value)}
                />
              ) : (
                user.name
              )}
            </td>
            <td>
              {editUserId === user.id ? (
                <input
                  type="email"
                  value={newEmail}
                  onChange={(e) => setNewEmail(e.target.value)}
                />
              ) : (
                user.email
              )}
            </td>
            <td>
              {comments[user.id] ? (
                <ul>
                  {comments[user.id].slice(0, 1).map((comment) => (
                    <li key={comment.id}>{comment.body}</li>
                  ))}
                </ul>
              ) : (
                <span>No comments</span>
              )}
            </td>
            <td>
              {editUserId === user.id ? (
                <button className="update-btn" onClick={handleUpdateClick}>
                  Update
                </button>
              ) : (
                <button className="edit-btn" onClick={() => handleEditClick(user)}>
                  Edit
                </button>
              )}
              <button className="delete-btn" onClick={() => onDelete(user.id)}>
                Delete
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default UserTable;
