import React from 'react'
import './user.css'

const User = ({id,email,name,onDelete}) => {

const handleDelete = () => {
onDelete(id);

}

const handleEdit = () => {
  if (isEditing) {
    onEdit(id, newName, newEmail);
  }
  setIsEditing(!isEditing);
};

  return (
    <div className='list'>
   {isEditing ? (
        <>
          <input
            type="text"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
          />
          <input
            type="email"
            value={newEmail}
            onChange={(e) => setNewEmail(e.target.value)}
          />
        </>
      ) : (
        <>
          <span>{name}</span>
          <span>{email}</span>
        </>
      )}
      <span>
      <button onClick={handleEdit}> edit </button>
      <button onClick={handleDelete}> delete </button>
     </span>
    </div>
  )
}

export default User
