// Components/UserTable.js
import React, { useEffect, useState } from 'react';
import './userTable.css';

const UserTable = ({ users, onDelete, onUpdate }) => {
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/albums/1/photos')
      .then((response) => response.json())
      .then((data) => setPhotos(data))
      .catch((error) => console.log('Error fetching photos:', error));
  }, []);

  return (
    <table className="user-table">
      <thead>
        <tr>
          <th>Photo</th>
          <th>Name</th>
          <th>Email</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {users.map((user, index) => (
          <tr key={user.id}>
            <td>
              <img
                src={photos[index % photos.length]?.thumbnailUrl}
                alt={`${user.name}'s avatar`}
                className="user-photo"
              />
            </td>
            <td>{user.name}</td>
            <td>{user.email}</td>
            <td>
              <button className="edit-btn" onClick={() => onUpdate(user.id, user.name, user.email)}>
                Edit
              </button>
              <button className="delete-btn" onClick={() => onDelete(user.id)}>
                Delete
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default UserTable;
